sourcesContent:
const express = require('express');
const routes = require('./routes/index');

const app = express();
const PORT = process.env.PORT || 5000;

app.use(express.json()); // Middleware to parse JSON bodies
app.use('/', routes);

app.listen(PORT, () => {
	  console.log(`Server is running on port ${PORT}`);
});

// Run worker 
require('./worker');
module.exports = app;

